INSERT INTO public.announcements (title, excerpt, content, pinned, published) VALUES
('New VIP Gaming Rooms Now Open!', 'Experience premium gaming in our newly renovated VIP rooms with state-of-the-art equipment and exclusive amenities.', 'We are excited to announce the opening of our renovated VIP gaming rooms! Each room features the latest gaming equipment, premium seating, and exclusive refreshment services. Book your VIP experience today and game like never before.', true, true),
('Weekend Tournament Series', 'Join our weekend gaming tournaments with amazing prizes and compete against the best players in the community.', 'Every weekend, we host competitive gaming tournaments across multiple games. Registration is now open for this month''s championship series. Prizes include gaming gear, cash rewards, and exclusive Pixoul Hub merchandise.', true, true),
('Extended Hours This Month', 'We are extending our operating hours to accommodate more gaming sessions during peak demand.', 'Due to popular demand, Pixoul Hub will be extending operating hours throughout this month. We will be open until 2 AM on weekends and midnight on weekdays. More gaming time for everyone!', true, true);